mapper.select:
     select ORIGIN_ACCOUNT.ORIGIN_ACCOUNT_MEMBERNAME from JOBORDER_PROCESS 
	 inner join ORIGIN_ACCOUNT on ORIGIN_ACCOUNT.ORIGIN_ACCOUNT_ID = JOBORDER_PROCESS.ORIGIN_ACCOUNT_ID
	 where JOBORDER_PROCESS_ID = #{JOBORDER_PROCESS_ID}

mapper.update:
update JOBORDER set
JOBORDER_INSPECTIONFILE = #{JOBORDER_INSPECTIONFILE}
where JOBORDER_ID = #{JOBORDER_ID}

update JOBORDER set
JOBORDER_WORKINSPECTIONFILE = #{JOBORDER_WORKINSPECTIONFILE}
where JOBORDER_ID = #{JOBORDER_ID}

mapper.select:
select * from SETTING_PRESET_FILE_MANAGEMENT where
SETTING_PRESET_FILE_MANAGEMENT_ID =
#{SETTING_PRESET_FILE_MANAGEMENT_ID}

mapper.update:
update JOBORDER set
JOBORDER_CODEFILE = #{JOBORDER_CODEFILE}
where JOBORDER_ID = #{JOBORDER_ID}

update JOBORDER_PROCESS set
