JOBORDER_LOBBY_ACTIVITY_ID3 = 7,
ORIGIN_ACCOUNT_ID = #{ORIGIN_ACCOUNT_ID}
where JOBORDER_PROCESS_ID = #{JOBORDER_PROCESS_ID} and JOBORDER_ID =
#{JOBORDER_ID}

update JOBORDER_PROCESS set
ORIGIN_ACCOUNT_ID = #{ORIGIN_ACCOUNT_ID}
where JOBORDER_PROCESS_ID = #{JOBORDER_PROCESS_ID} and JOBORDER_ID =
#{JOBORDER_ID} and PROCESS_MAIN_CATEGORY_ID =
#{PROCESS_MAIN_CATEGORY_ID}

