JOBORDER_EQUIPMENT_ID = #{JOBORDER_EQUIPMENT_ID}

mapper.delete:
delete from JOBORDER_DETAIL where JOBORDER_DETAIL_ID = #{JOBORDER_DETAIL_ID}
and (select JOBORDER_PUBLISH from JOBORDER where JOBORDER_ID =
#{JOBORDER_ID}) = 0

